export interface JDSActionButtonProps {
    /** It can be used to switch between 3 variants of ActionButton which is by default 'primary'. */
    kind?: 'primary' | 'secondary' | 'tertiary';
    /** it can be used to control the size of ActionButton. */
    size?: 'medium' | 'small' | 'large' | 'extra-large';
    /** It can be used to set the appearance of ActionButton. */
    appearance?: 'normal' | 'positive' | 'negative' | 'contrast';
    /** It can be used to change the state of ActionButton. */
    state?: 'normal' | 'disabled' | 'loading';
    /** It can be used to psoition icon. */
    iconPosition?: 'left' | 'right';
    /** This can be used to fill the container with ActionButton. */
    stretch?: boolean;
    /**  */
    type?: string;
    /** It displays label of ActionButton. */
    label?: string;
    /** It displays icon of ActionButton. */
    icon?: string | JSX.Element;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    ariaHidden?: string;
    iconAriaLabel?: string;
    custom?: string;
}
