export interface JDSAvatarProps {
    /** This can be used to control the size of Avatar. */
    size?: 'medium' | 'xtra-small' | 'small' | 'large' | 'xtra-large' | 'xtra-xtra-large';
    /** It can be used to switch between 3 variants of Avatar. */
    kind?: 'icon' | 'initials' | 'image';
    /** It can be used to enable or disable Avatar. */
    disabled?: boolean;
    /** It can be used to make Avatar Clickable */
    isClickable?: boolean;
    /** This is the two initials of user name. It will be used, when [kind] is set to initials. */
    initials?: string;
    /** This is a user uploaded image. It will be used, when [kind] is set to image. */
    image?: string;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    role?: string;
    ariaHaspopup?: string;
    ariaExpanded?: string;
    ariaControls?: string;
    custom?: string;
}
