export interface JDSBadgeProps {
    /** It can be used to switch between 2 variants of Badge. */
    kind?: 'normal' | 'service';
    /** It can be used to adjust the size of Badge. */
    size?: 'medium' | 'small' | 'large';
    /** It is used to display label of badge. */
    label?: string;
    /** It displays icon on the left with text. */
    icon?: string | JSX.Element;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
