export interface JDSButtonProps {
    /** It can be used to switch between 3 variants of Button which is by default 'primary'. */
    kind?: 'primary' | 'secondary' | 'tertiary';
    /** it can be used to control the size of Button. */
    size?: 'medium' | 'small' | 'large';
    /** It can be used to change the state of Button. */
    state?: 'normal' | 'destructive' | 'positive';
    /** It can be used to make Button full-width. */
    fullWidth?: boolean;
    /** It can be used to enable or disable Button. */
    disabled?: boolean;
    /** This can be used to add loading spinner to the right of label in Button. */
    loading?: boolean;
    /**  */
    type?: string;
    /** It displays label of Button. */
    label?: string;
    /** It can be used to add icon to the left of the text of Button. */
    iconLeft?: string | JSX.Element;
    /**  */
    description?: string;
    /** It adds icon to the right of the text of Button. */
    icon?: string | JSX.Element;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onBlur?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    ariaExpanded?: string;
    ariaControls?: string;
    iconAriaLabel?: string;
    custom?: string;
}
