export interface JDSDividerProps {
    /** Determines the view styles i.e. horizontal or vertical. */
    orientation?: 'horizontal' | 'vertical';
    /**  */
    variant?: 'default' | 'stepper';
    /** Sets the position of padding defined as part of `padding` property. If `padding` property present, this default to `all`. */
    padPosition?: 'all' | 'vertical' | 'horizontal' | 'bottom' | 'top' | 'left' | 'right';
    /**  */
    pad?: 'small' | 'base' | 'medium' | 'xtra-small' | 'large' | 'xtra-large' | 'xtra-xtra-large' | 'xtra-xtra-small' | 'huge' | 'massive' | 'none';
    /**  */
    label?: string;
    ariaHidden?: string;
    custom?: string;
}
