export interface JDSDropdownMenuProps {
    /**  */
    kind?: 'single-select' | 'multi-select';
    /**  */
    searchTrigger?: boolean;
    /**  */
    showCTAButtons?: boolean;
    /**  */
    labelTextWrap?: boolean;
    /**  */
    open?: boolean;
    /**  */
    width?: 'max-content' | 'trigger';
    /**  */
    position?: 'bottom_start' | 'bottom_end' | 'top_start' | 'top_end' | 'right_start' | 'right_end' | 'left_start' | 'left_end' | 'bottom_centre' | 'top_centre';
    /**  */
    searchLabel?: string;
    /**  */
    searchValue?: string;
    /**  */
    searchSuffix?: string | JSX.Element;
    /**  */
    triggerRef?: React.MutableRefObject<any>;
    /**  */
    primaryCTA?: string;
    /**  */
    secondaryCTA?: string;
    /**  */
    children?: React.ReactElement<any>;
    /**  */
    offsetX?: string | number;
    /**  */
    offsetY?: string | number;
    onSuffixClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onSearchChange?: () => void;
    onPrimaryClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onSecondaryClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onClose?: () => void;
    searchAriaLabel?: string;
    custom?: string;
}
