export interface JDSIconProps {
    /** It can be used to control size of icon. Please note that the actual size of the icon inside will vary based on the `kind` selected. */
    size?: 'medium' | 'small' | 'large' | 'xtra-large' | 'xtra-xtra-large';
    /**  */
    color?: 'primary_50' | 'primary_30' | 'primary_60' | 'primary_70' | 'primary_background' | 'secondary_50' | 'sparkle_50' | 'primary_grey_60' | 'primary_grey_80' | 'primary_grey_100' | 'feedback_success' | 'feedback_error' | 'feedback_warning' | 'feedback_success_50' | 'feedback_error_50' | 'feedback_warning_50' | 'white' | 'none';
    /** It can be used to switch between 3 variants of Icon. 'background-bold' sets color prop as bg, Icon color is inverse of that. */
    kind?: 'default' | 'background' | 'background_bold' | 'inverse';
    /**  */
    customSize?: string | number;
    /** It can take the icon user want to pass or the name of icon. e.g. `ic={<IcFavorite/>}` or `ic='IcFavorite'`. User can also pass a any custom icon he may have stored locally. */
    ic?: string | JSX.Element;
    /**  */
    semantics?: string;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    custom?: string;
}
