export interface JDSImageProps {
    /**  */
    aspectRatio?: 'auto' | '1:1' | '4:3' | '16:9' | '3:4' | '9:16' | '2:75:1';
    /**  */
    shape?: 'sharp' | 'rounded' | 'circle';
    /**  */
    fit?: 'fill' | 'contain' | 'cover' | 'none' | 'scale-down' | 'inherit' | 'initial' | 'revert' | 'revert-layer' | 'unset';
    src?: string;
    /**  */
    width?: string;
    /**  */
    height?: string;
    /**  */
    alt?: string;
    /**  */
    loading?: 'auto' | 'eager' | 'lazy' | 'error' | 'empty';
    /**  */
    crossOrigin?: 'anonymous' | 'use-credentials';
    /**  */
    srcSet?: string;
    /** A resource that is displayed while the image is loading. */
    placeholder?: string;
    /** A resource that is displayed when the image request is unsuccessful. */
    error?: string;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onError?: () => void;
    onLoading?: () => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
