import { MutableRefObject } from 'react';
export type ErrorFields = {
    /** Error in date field */
    dd?: boolean;
    /** Error in month field */
    mm?: boolean;
    /** Error in year field */
    yyyy?: boolean;
};
export type ErrorFieldsWithText = ErrorFields & {
    errorLocalText?: string;
};
export type DateType = string | Date;
export type PartialDate = {
    date?: string;
    month?: string;
    year?: string;
};
export type InputDateProps = {
    /**
     * Determines the value
     * string type format : mm-dd-yyyy
     * date type format : new Date()
     */
    value?: string | Date;
    /**
     * The error state of the input
     */
    error?: boolean;
    /** The error text to display */
    errorText?: string;
    /**
     * Determines the required input field
     */
    required?: boolean;
    /**
     * Determines the disabled input field
     */
    disabled?: boolean;
    /**
     * Determines the label
     */
    label: string;
    /**
     * Callback to set custom validity. Use this to set the invididual
     * fields of the date input to error states.
     * @param date The input value
     */
    setCustomValidity?: (date: DateType) => ErrorFieldsWithText;
    /**
     * Callback used to determine whether a given date is valid.
     * Use this to override the default checks to max and min.
     * @param date The input value
     */
    setValidity?: (value: string | Date) => boolean;
    /**
     * On input change event call
     * @param value - The Input value
     */
    onChange?: (value: string) => void;
    /**
     * The helper text to display
     */
    helperText?: string;
    /**
     * Enables Date Picker
     */
    datePicker?: boolean;
    /**
     * Defines the min date possible
     */
    min?: DateType;
    /**
     * Defines the max date possible
     */
    max?: DateType;
    /**
     * Use this to obtain the value of Input Date
     */
    forwardRef?: MutableRefObject<string | Date | null | undefined>;
    /**
     * Determines the interval between dates
     */
    step?: string | number;
    /**
     * Use this to set individual error fields
     * {dd:false, mm:false, yyyy:false}
     */
    errorFieldsObject?: ErrorFields;
    dateAriaLabel?: string;
    monthAriaLabel?: string;
    yearAriaLabel?: string;
    datepickerAriaLabel?: string;
};
