export interface JDSModalMobileProps {
    /** It can be used to switch between 3 variants of Modal. `informational` modal has no buttons,`dialog` modal has two buttons, `acknowledgement` modal has only a single button. */
    kind?: 'dialog' | 'acknowledgement' | 'informational' | 'custom';
    /** It can be used to control the size of popup container. */
    size?: 'medium' | 'small';
    /** It is used to align header of Modal. */
    headerAlignment?: 'left' | 'right' | 'center';
    /** It can be used to makes Modal closeable, displays close icon button on popup. */
    isCloseable?: boolean;
    /** It is used to close the Modal. */
    close?: boolean;
    /** It determines whether modal should close and the onClose event should be triggered when clicking outside modal. */
    disableOutsideClick?: boolean;
    /** It displays the title of modal. */
    header?: string;
    /** It displays label of primary button of Modal. */
    primaryCTA?: string;
    /**  */
    primaryCtaDisabled?: boolean;
    /** It displays label of secondary button of Modal. */
    secondaryCTA?: string;
    /**  */
    secondaryCtaDisabled?: boolean;
    /**  */
    children?: React.ReactElement<any>;
    onClose?: () => void;
    onPrimaryClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onSecondaryClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    cancelButtonCtaAriaLabel?: string;
    headerAriaLabel?: string;
    primaryCtaAriaLabel?: string;
    primaryCtaAriaDescribedby?: string;
    secondaryCtaAriaLabel?: string;
    secondaryCtaAriaDescribedby?: string;
    custom?: string;
}
