import type { JDSFeedbackBlockProps } from '../../internal';
export interface JDSProgressBarProps {
    /** It can be used to switch between 2 variants of progess bar. */
    kind?: 'determinate' | 'indeterminate';
    /** It can be used align label of progress bar to center or at the ends of progress bar. */
    labelPosition?: 'stretch' | 'center';
    /** It can be used to choose between states i.e. 'success','warning','error','none'.By default it is 'none'.This can be dynamically changed as per user's form handling. */
    state?: JDSFeedbackBlockProps['state'];
    /** It displays the main label of Progess Bar. */
    label?: string;
    /** It displays the support label of Progess Bar. */
    valueLabel?: string;
    /** It takes user input for value of progress bar. */
    value?: number;
    ariaLabel?: string;
    ariaBusy?: string;
    ariaDescribedby?: string;
    custom?: string;
}
