import type { JDSTextProps, JDSImageProps } from '../../internal';
export interface JDSPromoCardProps {
    /** Determines the size of the PromoCard. Size dictates the text and spacing. */
    size?: 'small' | 'xtra-small' | 'medium' | 'large';
    /** Horizontal orientation puts image and content side by side. */
    orientation?: 'vertical' | 'horizontal';
    /**  */
    imageRatio?: 'wide' | 'square' | 'landscape' | 'portrait';
    /**  */
    imageFocus?: JDSImageProps['imageFocus'];
    /** Toggle drop shadow */
    shadow?: boolean;
    /** The main title copy. */
    title?: string;
    /** Accepts description as string. JSX.Element is also allowed in case you have anchor tags inside your richtext. */
    description?: string;
    /**  */
    primaryCTA?: string;
    /**  */
    children?: React.ReactElement<any>;
    /**  */
    image?: string;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onPrimaryClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    footerPrefix?: React.ReactElement<JDSTextProps>;
    footerSuffix?: React.ReactElement<JDSTextProps>;
    primaryCtaAriaLabel?: string;
    primaryCtaAriaDescribedby?: string;
    secondaryCtaAriaLabel?: string;
    secondaryCtaAriaDescribedby?: string;
    imageAriaLabel?: string;
    imageAriaDescribedby?: string;
    custom?: string;
}
