export interface JDSSearchBoxProps {
    /** It determines if SearchBox is to be used on header or normal background. */
    kind?: 'normal' | 'header' | 'light_background';
    /** It can be used to enable or disable SearchBox. */
    disabled?: boolean;
    /** It takes user input for text to be searched. */
    value?: string;
    /** It displays name of search box. */
    name?: string;
    /** It displays placeholder text of search box. */
    label?: string;
    /**  */
    maxLength?: number;
    /** It can be used to pass icon as prefix. */
    prefix?: string | JSX.Element;
    /** It can be used to display icon on the right of search box. */
    suffix?: string | JSX.Element;
    /**  */
    showBack?: boolean;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onBlur?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onChange?: () => void;
    onSubmit?: () => void;
    onKeyup?: () => void;
    onKeyDown?: () => void;
    onBack?: () => void;
    onSuffixClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    prefixAriaLabel?: string;
    suffixAriaLabel?: string;
    ariaLabel?: string;
    custom?: string;
}
