export interface JDSSearchResultsInternalProps {
    /** This determines the appearance of search results, it can be 'normal or 'box'. */
    appearance?: 'normal' | 'box';
    /**  */
    maxWidth?: string | number;
    /** It is a heading that will display in the Header of the SearchResults. */
    header?: string;
    /** If this is passed a button will appear on the right side of the Heading in the Header. */
    headerAction?: string;
    /**  */
    results?: Array<{
        resultAriaLabel?: string;
        resultAriaDescribedby?: string;
        icon?: string | JSX.Element;
        label?: string;
    }>;
    /**  */
    query?: string;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onHeaderActionClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onContainerClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    headerActionAriaLabel?: string;
    headerActionAriaDescribedby?: string;
    ariaHidden?: string;
    custom?: string;
}
