export interface JDSSelectorButtonProps {
    /**  */
    kind?: 'primary' | 'secondary' | 'tertiary' | 'tertiary_narrow';
    /**  */
    iconPosition?: 'left' | 'right';
    /**  */
    appearance?: 'normal' | 'grey_scale' | 'contrast';
    /** Sizes of the button */
    size?: 'medium' | 'small' | 'large' | 'extra_large';
    /** It can be used to enable or disable SelectorButton. */
    disabled?: boolean;
    /**  */
    selected?: boolean;
    /**  */
    stretch?: boolean;
    /**  */
    type?: string;
    /**  */
    text?: string;
    /**  */
    icon?: string | JSX.Element;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onBlur?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
