import type { JDSFeedbackBlockProps } from '../../internal';
export interface JDSSliderProps {
    /**  It allows user to input their own number in the text box that moves the slider value. By default this textbox is in readonly mode. */
    editableTextBox?: boolean;
    /** It can be used to choose between states i.e. 'success','warning','error','none'. */
    state?: JDSFeedbackBlockProps['state'];
    /** It can be used to enable or disable SearchBox. */
    disabled?: boolean;
    stateText?: string;
    /** It displays label of Slider. */
    label?: string;
    /**  */
    valueSuffix?: string;
    /**  */
    id?: string;
    /**  */
    name?: string;
    /**  */
    placeholder?: string;
    /** It is minimum value for Slider. */
    min?: number;
    /** It is maximum value for Slider. */
    max?: number;
    /**  */
    required?: boolean;
    /** It determines number of divisions slider should slide by. */
    step?: number;
    /** It takes user input for value of Slider. */
    value?: number;
    /**  */
    labelMin?: string;
    /**  */
    labelMax?: string;
    /** It displays helper text of Slider. */
    helperText?: string;
    onValueChange?: () => void;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onBlur?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onChange?: () => void;
    onSubmit?: () => void;
    onKeyDown?: () => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
