export interface JDSSpinnerProps {
    /** It determines the position of the label. Note: if using a small spinner, the label can only be positioned on the right. */
    labelPosition?: 'right' | 'bottom';
    /** It can be used to control size od Spinner. */
    size?: 'medium' | 'small';
    /** It determines the appearance of the spinner .`inline` inherits from text color,`normal`: single color, `vibrant`: three color. */
    kind?: 'normal' | 'inline' | 'vibrant';
    /**  */
    isOverlay?: boolean;
    /** It displays the label of Spinner. */
    label?: string;
    /** It can be used to take value of spinner from user. */
    value?: number;
    ariaLabel?: string;
    ariaDescribedby?: string;
    ariaLive?: string;
    custom?: string;
}
