export interface JDSTagProps {
    /** It can be used to control size of Tag. */
    size?: 'medium' | 'small';
    /** It can be used to switch between 2 variants of Tag. */
    kind?: 'toggle' | 'filter';
    /** It can be used to enable or disable Tag. */
    disabled?: boolean;
    /** This can be used to activate Tag. */
    active?: boolean;
    /** It displays label of Tag. */
    label?: string;
    /**  */
    icon?: string | JSX.Element;
    onClick?: (ev: React.MouseEvent<HTMLButtonElement>) => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
