import type { JDSFeedbackBlockProps } from '../../internal';
export interface JDSTextareaProps {
    /** It can be used to enable or disable SearchBox. */
    disabled?: boolean;
    /** It can change the border of textArea */
    border?: boolean;
    /** It can be used to choose between states i.e. 'success','warning','error','none'. */
    state?: JDSFeedbackBlockProps['state'];
    /** It is used when border is set to true */
    readOnly?: boolean;
    /**  */
    id?: string;
    /** It displays label of Textarea. */
    label?: string;
    /**  */
    name?: string;
    /**  */
    value?: string;
    /**  */
    rows?: number;
    /**  */
    minLines?: number;
    /**  */
    placeholder?: string;
    /**  */
    maxLength?: number;
    /**  */
    required?: boolean;
    /** It displays message with respect to its [state] selected. */
    stateText?: string;
    /** It displays helper text of Textarea. */
    helperText?: string;
    onFocus?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onBlur?: (ev: React.FocusEvent<HTMLButtonElement>) => void;
    onChange?: () => void;
    onKeyDown?: () => void;
    onInvalid?: () => void;
    onSubmit?: () => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    ariaInvalid?: string;
    custom?: string;
}
