export interface JDSTooltipProps {
    /**  */
    position?: 'right' | 'right_start' | 'right_end' | 'bottom' | 'bottom_start' | 'bottom_end' | 'left' | 'left_start' | 'left_end' | 'top' | 'top_start' | 'top_end';
    /** It can be used to progamtically open and close Tooltip. */
    open?: boolean;
    /** It displays label of Tooltip. */
    label?: string;
    /**  */
    triggerRef?: React.MutableRefObject<any>;
    /**  */
    width?: string | number;
    /**  */
    triggerSpacing?: string | number;
    /**  */
    offsetX?: string | number;
    /**  */
    offsetY?: string | number;
    onHover?: () => void;
    ariaLabel?: string;
    ariaDescribedby?: string;
    custom?: string;
}
