/**
 * Measures and console logs the performance of the given function
 * @param func Zero Argument Function to measure time of
 * @returns Return value of function
 */
declare function performanceWrapper<T>(func: () => T, details?: string): T;
declare const assignDefaultValues: (reference: ThisType<unknown>, defaultValues: Record<string, unknown>) => ThisType<unknown>;
declare const isHexValid: (hex: string) => boolean;
export { performanceWrapper, assignDefaultValues, isHexValid };
