export type Primitive = string | number | boolean;
export type PrimitiveMap = Record<string, Primitive>;
export type ComponentTokens = Record<string, Primitive>;
export type TokenMap = Record<string, string | number | boolean | Array<number>>;
export type TokensGroup = Record<string, TokenMap>;
export type TokensWithGetter = {
    tokens: TokensGroup;
    getSwitchableTokens?: (tokens: Record<string, string>) => TokensGroup;
    getComponentTokens?: (componentName: string) => ComponentTokens | undefined;
};
